'use strict';


describe('star', function() {
    beforeEach(module('ADE'));

    var starFilter;

    beforeEach(inject(function($filter) {
        starFilter = $filter('toggle');
    }));

    it('should return background position of a filled star', function() {
        expect(starFilter(true,'star')).toEqual('<span class="ade-star ade-on">');
        expect(starFilter(1,'star')).toEqual('<span class="ade-star ade-on">');
        expect(starFilter('true','star')).toEqual('<span class="ade-star ade-on">');
        expect(starFilter('anything','star')).toEqual('<span class="ade-star ade-on">');
        expect(starFilter('yes','star')).toEqual('<span class="ade-star ade-on">');
    });

    it('should return background position of a hollow star', function() {
        expect(starFilter(false,'star')).toEqual('<span class="ade-star ade-off">');
        expect(starFilter(0,'star')).toEqual('<span class="ade-star ade-off">');
        expect(starFilter('false','star')).toEqual('<span class="ade-star ade-off">');
        expect(starFilter('0','star')).toEqual('<span class="ade-star ade-off">');
        expect(starFilter('no','star')).toEqual('<span class="ade-star ade-off">');
    });
});
